import { Link, useLocation } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { Helmet } from 'react-helmet-async';

export default function SigninScreen() {
  const { search } = useLocation(); //its a hook
  const redirectInUrl = new URLSearchParams(search).get('redirect');
  const redirect = redirectInUrl ? redirectInUrl : '/';
  return (
    // we create a container to make the sign in form
    <Container className="small-container">
      <Helmet>
        <title>Sign In</title>
      </Helmet>
      <h1 className="my-3">Sign In</h1>{' '}
      {/* my-3 means margin from top and bottom is 3 rem*/}
      <Form>
        <Form.Group className="mb-3" controlId="email">
          {/*we use form group to put label and input box next to each other */}
          <Form.Label>Email</Form.Label>
          <Form.Control type="email" required /> {/*creates an input box */}
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control type="password" required />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit">Sign In</Button>
        </div>
        <div className="mb-3">
          New customer? {/*if  customer is new */}
          {/*and if new we will redirect user to the sign up page and seet query string for
          redirect to the redirect variable that we are going to fetch from URL*/}
          <Link to={`/signup?redirect=${redirect}`}>Create your account</Link>
        </div>
      </Form>
    </Container>
  );
}
