import { useContext } from 'react';
import { Store } from '../Store';
import { Helmet } from 'react-helmet-async';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import MessageBox from '../components/MessageBox';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';

export default function CartScreen() {
  //in this function we are going to access the context
  const { state, dispatch: ctxDispatch } = useContext(Store); //as we did in product screen

  //from state we construct cart and from cart we construct cartItems beacuse we will list cartItems in this screen
  const {
    cart: { cartItems },
  } = state;

  return (
    <div>
      <Helmet>
        <title>Shopping Cart</title>
      </Helmet>
      <h1>Shopping Cart</h1>
      <Row>
        <Col md={8}>
          {/*here we show list of items, we use conditional rendering */}
          {cartItems.length === 0 ? (
            <MessageBox>
              Cart is empty. <Link to="/">Go Shopping</Link>
            </MessageBox>
          ) : (
            {
              /*otherwise we have a list of items */
            }(
              <ListGroup>
                {/*use map function in cart items */}
                {cartItems.map((item) => (
                  <ListGroup.Item key={item._id}>
                    <Row className="align-items-center">
                      <Col md={4}>
                        <img
                          src={item.image}
                          alt={item.name}
                          className="img-fluid rounded img-thumbnail"
                        ></img>{' '}
                        {/*=> this is a space */}
                        <Link to={`/product/${item.slug}`}>{item.name}</Link>
                      </Col>
                      <Col md={3}>
                        <Button variant="light" disabled={item.quantity === 1}>
                          {' '}
                          {/*disable if count is = 1 */}
                          <i className="fas fa-minus-circle"></i>{' '}
                          {/*from fontawesome */}
                        </Button>{' '}
                        <span>{item.quantity}</span>{' '}
                        <Button
                          variant="light"
                          disabled={item.quantity === item.countInStock}
                        >
                          <i className="fas fa-plus-circle"></i>
                        </Button>{' '}
                      </Col>
                      <Col md={3}>${item.price}</Col>
                      <Col md={2}>
                        {' '}
                        {/*this is for deletion */}
                        <Button variant="light">
                          <i className="fas fa-trash"></i>
                        </Button>
                      </Col>
                    </Row>
                  </ListGroup.Item>
                ))}
              </ListGroup>
            )
          )}
        </Col>
        <Col md={4}></Col>
      </Row>
    </div>
  );
}
