import axios from 'axios';
import { useContext, useEffect, useReducer } from 'react';
import { useParams } from 'react-router-dom';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import ListGroup from 'react-bootstrap/ListGroup';
import Badge from 'react-bootstrap/Badge';
import Rating from '../components/Rating';
import Button from 'react-bootstrap/Button';
import { Helmet } from 'react-helmet-async';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { getError } from '../utils';
import { Store } from '../Store';

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true };
    //loading is a state , ...state return previous state values
    //loading is true so that we can show a loading box in UI and we have kept prvious state values
    case 'FETCH_SUCCESS':
      return { ...state, product: action.payload, loading: false };
    //previous values are kept in the state and only update products = data coming from the action
    //data is present in action.payload ,ie, all data from backend is in action.payload
    case 'FETCH_FAIL':
      return { ...state, loading: false, error: action.payload };
    default:
      return state;
  }
};

function ProductScreen() {
  //useParams returns an object of key/value pairs of URL parameters
  const params = useParams();
  const { slug } = params;

  const [{ loading, error, product }, dispatch] = useReducer(reducer, {
    product: [], //er are using product not products because we want to show only 1 product
    loading: true,
    error: '',
  });
  //first parameter is an object and second parameter is disptch it is used to call an action and
  //update the state
  //const [products, setProducts] = useState([]);
  //useState allows to track state, state refers to data or properties that need tracking
  //here products is a variable ,ie, current state and setProducts is a function that can update this state
  //we have used destructuring . The initial state is empty array as indicated inside useState([]) function
  //We can now include our state anywhere in our component.
  //useState returns an array that contains a variable and function to update that variable

  useEffect(() => {
    const fetchData = async () => {
      //before AJAX request we need to set loading to true
      dispatch({ type: 'FETCH_REQUEST' });
      try {
        const result = await axios.get(`/api/products/slug/${slug}`);
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data });
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: getError(err) });
      }

      //setProducts(result.data); //result.data is product in the backend
      //async function returs a promise which can be pending, fulfilled, rejected
      //await makes a function wait for a promise
      //axios.get sends a AJAX request to the following address and puts the result in result variable
      //AJAX request allows to read data from web server
    };
    fetchData();
  }, [slug]); //slug is a dependency, when there is a change in slug (when user switches between pages)
  //fetchData will dispatch again and we get new product from backend

  const { state, dispatch: ctxDispatch } = useContext(Store);
  //we have renamed dispatch to distinguish it from dispatch of current component

  const { cart } = state;

  const addToCartHandler = async () => {
    //this function adds items to cart
    //to add item in cart we need to disptach an action on the react context
    const existItem = cart.cartItems.find((x) => x._id === product._id);
    //to find if current product exists in cart or not
    const quantity = existItem ? existItem.quantity + 1 : 1;
    const { data } = await axios.get(`/api/products/${product._id}`);
    if (data.countInStock < quantity) {
      window.alert('Sorry. Product is out of stock');
      return;
    }
    ctxDispatch({
      type: 'CART_ADD_ITEM',
      payload: { ...product, quantity: 1 },
    });
  };

  return (
    //this is the UI part
    //we are using conditional rendering
    loading ? (
      <LoadingBox />
    ) : error ? (
      <MessageBox variant="danger">{error}</MessageBox>
    ) : (
      <div>
        <Row>
          <Col md={6}>
            <img
              className="img-large"
              src={product.image}
              alt={product.name}
            ></img>
          </Col>
          <Col md={3}>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Helmet>
                  <title>{product.name}</title>
                </Helmet>
                <h1>{product.name}</h1>
              </ListGroup.Item>
              <ListGroup.Item>
                <Rating
                  rating={product.rating}
                  numReviews={product.numReviews}
                ></Rating>
              </ListGroup.Item>
              <ListGroup.Item>Pirce : Rs.{product.price}</ListGroup.Item>
              <ListGroup.Item>
                Description:
                <p>{product.description}</p>
              </ListGroup.Item>
            </ListGroup>
          </Col>
          <Col md={3}>
            <Card>
              <Card.Body>
                {/* we are using card body because we will add padding */}
                <ListGroup variant="flush">
                  {/*using flush removes border */}
                  <ListGroup.Item>
                    <Row>
                      <Col>Price:</Col>
                      <Col>Rs.{product.price}</Col>
                    </Row>
                  </ListGroup.Item>
                  <ListGroup.Item>
                    <Row>
                      <Col>Status:</Col>
                      <Col>
                        {product.countInStock > 0 ? (
                          <Badge bg="success">In Stock</Badge>
                        ) : (
                          <Badge bg="danger">Unavailable</Badge>
                        )}
                      </Col>
                    </Row>
                  </ListGroup.Item>
                  {product.countInStock > 0 && (
                    <ListGroup.Item>
                      <div className="d-grid">
                        <Button onClick={addToCartHandler} variant="primary">
                          Add to Cart
                        </Button>
                      </div>
                    </ListGroup.Item>
                  )}
                </ListGroup>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </div>
    )
  );
}
export default ProductScreen;
